import React, { createContext, useReducer, useEffect } from 'react';
import { todoReducer } from '../reducers/todoReducer';

export const BookContext = createContext();

const BookContextProvider = (props) => {
  // Whenever we refresh the local storage again become zero so we have to pass third function inside useReducer
  // Now initial value of state is equal to books stored in local storage
  const [books, dispatch] = useReducer(bookReducer, [], () => {
    const localData = localStorage.getItem('books');
    return localData ? JSON.parse(localData) : [];
  });
  useEffect(() => {
    localStorage.setItem('books', JSON.stringify(books));
    // this hook will run whenever there is change in books
  }, [books]);
  return (
    <TodoContext.Provider value={{ todos, dispatch }}>
      {props.children}
    </TodoContext.Provider>
  );
}

export default TodoContextProvider;
